// src/components/ApplicationForm.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/ApplicationForm.css';

const ApplicationForm = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        fullName: '',
        email: '',
        cv: null,
        coverLetter: '',
    });

    const handleInputChange = (e) => {
        const { id, value, files } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [id]: files ? files[0] : value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const { fullName, email, cv, coverLetter } = formData;

        if (!fullName || !email || !cv || !coverLetter) {
            alert('Please fill in all the required information');
            return;
        }

        const data = new FormData();
        data.append('fullName', fullName);
        data.append('email', email);
        data.append('cv', cv);
        data.append('coverLetter', coverLetter);

        try {
            const response = await fetch('http://localhost:3001/application-form', { // Adjust the URL as necessary
                method: 'POST',
                body: data,
            });
            if (response.ok) {
                alert('Submit Successful');
                navigate('/job-search'); // Adjust the route to your job search page
            } else {
                alert('Failed to submit the application');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('An error occurred while submitting the application');
        }
    };

    const handleCancel = () => {
        navigate('/job-details'); // Adjust the route to your job details page
    };

    return (
        <div className="container">
            <h2>Job Application Form</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="fullName">Full Name:</label>
                    <input
                        type="text"
                        id="fullName"
                        value={formData.fullName}
                        onChange={handleInputChange}
                        placeholder="Enter your full name"
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="email">Email Address:</label>
                    <input
                        type="email"
                        id="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        placeholder="Enter your email address"
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="cv">Upload CV:</label>
                    <input
                        type="file"
                        id="cv"
                        accept=".pdf" // Ensure that only PDF files can be uploaded
                        onChange={handleInputChange}
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="coverLetter">Cover Letter:</label>
                    <textarea
                        id="coverLetter"
                        value={formData.coverLetter}
                        onChange={handleInputChange}
                        placeholder="Write your cover letter"
                    />
                </div>

                <div className="button-container">
                    <button className="button" type="submit">Submit Application</button>
                    <button className="button cancel" type="button" onClick={handleCancel}>Cancel</button>
                </div>
            </form>
        </div>
    );
};

export default ApplicationForm;
