import React from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

const Register = () => {
    const navigate = useNavigate(); // Khởi tạo navigate

    const handleSubmit = async (e) => {
        e.preventDefault();
        const fullname = e.target.elements.fullname.value;
        const email = e.target.elements.email.value;
        const password = e.target.elements.password.value;
        const user_type = e.target.elements.user_type.value;

        try {
            const res = await fetch('http://localhost:3001/register', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ fullname, email, password, user_type })
            });

            if (res.ok) {
                const data = await res.json();
                alert('Đăng ký thành công');

                // Dựa vào user_type để chuyển hướng đến đúng trang
                if (data.user_type === 'applicant') {
                    navigate('/applicant-home');
                } else if (data.user_type === 'recruiter') {
                    navigate('/recruiter-home');
                }
            } else {
                alert('Đăng ký thất bại');
            }
        } catch (error) {
            console.error('Có lỗi xảy ra:', error);
            alert('Có lỗi xảy ra khi đăng ký.');
        }
    };

    return (
        <div className="container">
            <h2>Register</h2>
            <form onSubmit={handleSubmit}>
                <label htmlFor="fullname">Full Name</label>
                <input type="text" id="fullname" name="fullname" required />

                <label htmlFor="email">Email Address</label>
                <input type="email" id="email" name="email" required />

                <label htmlFor="password">Password</label>
                <input type="password" id="password" name="password" required />

                <label htmlFor="user_type">User Type</label>
                <select id="user_type" name="user_type">
                    <option value="applicant">Applicant</option>
                    <option value="recruiter">Recruiter</option>
                </select>

                <button type="submit" className="button">Register</button>
            </form>
        </div>
    );
};

export default Register;
