// src/components/ApplicationForm.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/ApplicationForm.css';

const ApplicationForm = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        fullName: '',
        email: '',
        cv: null,
        coverLetter: '',
    });

    const handleInputChange = (e) => {
        const { id, value, files } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [id]: files ? files[0] : value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const formDataToSend = new FormData();
        formDataToSend.append('fullName', formData.fullName); // Sử dụng formData.fullName
        formDataToSend.append('email', formData.email); // Sử dụng formData.email
        formDataToSend.append('coverLetter', formData.coverLetter); // Sử dụng formData.coverLetter
        formDataToSend.append('cv', formData.cv); // Sử dụng formData.cv

        try {
            const response = await fetch('http://localhost:3001/api/apply', {
                method: 'POST',
                body: formDataToSend,
            });


            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const data = await response.text();
            console.log('Success:', data);
            navigate('/job-details'); // Chuyển hướng đến trang job-details sau khi thành công
        } catch (error) {
            console.error('Error:', error);
        }
    };
    const handleJobDetail = (job) => {
        navigate('/job-details', { state: { job } }); // Đảm bảo bạn đang truyền đối tượng job
    };


    const handleCancel = () => {
        navigate('/job-details'); // Điều chỉnh đường dẫn đến trang job details của bạn
    };

    return (
        <div className="container">
            <h2>Job Application Form</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="fullName">Full Name:</label>
                    <input
                        type="text"
                        id="fullName"
                        value={formData.fullName}
                        onChange={handleInputChange}
                        placeholder="Enter your full name"
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="email">Email Address:</label>
                    <input
                        type="email"
                        id="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        placeholder="Enter your email address"
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="cv">Upload CV:</label>
                    <input
                        type="file"
                        id="cv"
                        accept=".pdf" // Đảm bảo chỉ cho phép tệp PDF được tải lên
                        onChange={handleInputChange}
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="coverLetter">Cover Letter:</label>
                    <textarea
                        id="coverLetter"
                        value={formData.coverLetter}
                        onChange={handleInputChange}
                        placeholder="Write your cover letter"
                    />
                </div>

                <div className="button-container">
                    <button onClick={() => handleJobDetail(job)} className="apply-button">Apply</button>
                    <button className="button cancel" type="button" onClick={handleCancel}>Cancel</button>
                </div>
            </form>
        </div>
    );
};

export default ApplicationForm;
