import React, { useState } from 'react';
import './JobPostingModal.css'; // Thêm file CSS nếu cần

const JobPostingModal = ({ isOpen, onRequestClose }) => {
    const [jobTitle, setJobTitle] = useState('');
    const [jobDescription, setJobDescription] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        // Xử lý lưu job posting ở đây
        console.log({ jobTitle, jobDescription });
        // Đóng modal sau khi lưu
        onRequestClose();
    };

    return (
        <div className={`modal ${isOpen ? 'open' : ''}`}>
            <div className="modal-content">
                <span className="close" onClick={onRequestClose}>&times;</span>
                <h2>Create New Job Posting</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="job-title">Job Title:</label>
                        <input
                            type="text"
                            id="job-title"
                            value={jobTitle}
                            onChange={(e) => setJobTitle(e.target.value)}
                            placeholder="Enter job title"
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="job-description">Job Description:</label>
                        <textarea
                            id="job-description"
                            value={jobDescription}
                            onChange={(e) => setJobDescription(e.target.value)}
                            placeholder="Enter job description"
                            required
                        />
                    </div>
                    <button className="button" type="submit">Save</button>
                    <button className="button cancel" type="button" onClick={onRequestClose}>Cancel</button>
                </form>
            </div>
        </div>
    );
};

export default JobPostingModal;
