// src/components/JobDetails.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/JobDetails.css'; // Import the CSS file for styling

const JobDetails = () => {
    const navigate = useNavigate();

    // Function to navigate to application form page
    const handleApplicationForm = () => {
        navigate('/application-form'); // Adjust the route as needed for your application form page
    };

    return (
        <div className="container">
            <h2>Job Details</h2>
            <div className="job-info">
                <p><strong>Job Title:</strong> Software Engineer</p>
                <p><strong>Company Name:</strong> TechCorp Inc.</p>
                <p><strong>Job Description:</strong></p>
                <textarea
                    readOnly
                    value="This is a detailed job description for the software engineer position. The candidate will work on various development tasks and projects, collaborate with cross-functional teams, and ensure the delivery of high-quality software solutions."
                />
                <p><strong>Required Skills:</strong> JavaScript, React, Node.js, Agile Methodologies</p>
                <p><strong>Salary Range:</strong> $80,000 - $100,000</p>
            </div>

            <div className="buttons">
                <button onClick={handleApplicationForm} className="button apply-now">Apply Now</button>
                <button className="button save">Save Job</button>
            </div>
        </div>
    );
};

export default JobDetails;
