// src/components/ViewCandidateProfile.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ViewCandidateProfile = () => {
    const [profile, setProfile] = useState(null);

    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const response = await axios.get('/api/candidate-profile'); // API để lấy hồ sơ ứng viên
                setProfile(response.data);
            } catch (error) {
                console.error('Lỗi khi lấy hồ sơ ứng viên:', error);
            }
        };
        fetchProfile();
    }, []);

    if (!profile) return <p>Đang tải...</p>;

    return (
        <div className="profile-container">
            <h2>Hồ Sơ Ứng Viên</h2>
            <p><strong>Họ Tên:</strong> {profile.full_name}</p>
            <p><strong>Email:</strong> {profile.email}</p>
            <p><strong>Hình Ảnh Hồ Sơ:</strong> <img src={profile.profile_picture} alt="Profile" /></p>
            <p><strong>Kinh Nghiệm Làm Việc:</strong> {profile.work_experience}</p>
            <p><strong>Học Vấn:</strong> {profile.education}</p>
            <button onClick={() => window.location.href = '/candidate-profile'}>Chỉnh Sửa</button>
        </div>
    );
};

export default ViewCandidateProfile;
