import React, { useState } from 'react';
import Modal from 'react-modal';
import '../styles/JobPostingModal.css';
import { useNavigate } from 'react-router-dom';

Modal.setAppElement('#root');

const JobPostingModal = ({ isOpen, onRequestClose, onAddJobPosting, editingJob }) => {
    const [jobTitle, setJobTitle] = useState(editingJob?.job_title || '');
    const [jobDescription, setJobDescription] = useState(editingJob?.job_description || '');
    const [requiredSkills, setRequiredSkills] = useState(editingJob?.required_skills || '');
    const [experience, setExperience] = useState(editingJob?.experience || '');
    const [salaryRange, setSalaryRange] = useState(editingJob?.salary_range || '');
    const [expiryDate, setExpiryDate] = useState(editingJob?.expiry_date || '');
    const [jobType, setJobType] = useState(editingJob?.job_type || 'full-time');
    const [postedDate] = useState(editingJob?.posted_date || new Date().toISOString());

    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('http://localhost:3001/job-postings', {
                method: editingJob ? 'PUT' : 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    jobTitle,
                    jobDescription,
                    requiredSkills,
                    experience,
                    salaryRange,
                    expiryDate,
                    jobType,
                    postedDate
                })
            });

            if (!response.ok) {
                throw new Error(`Error: ${response.statusText}`);
            }

            const data = await response.json();
            onAddJobPosting(data);
            onRequestClose();
            navigate('/job-postings');
        } catch (error) {
            console.error('Error saving job posting:', error);
        }
    };

    return (
        <Modal isOpen={isOpen} onRequestClose={onRequestClose} contentLabel="Create/Edit Job Posting">
            <h2>{editingJob ? 'Edit Job Posting' : 'Create Job Posting'}</h2>
            <form onSubmit={handleSubmit}>
                {/* Form fields */}
                {/* Same form structure as before */}
                <div className="button-container">
                    <button className="button" type="submit">Save</button>
                    <button className="button cancel" type="button" onClick={onRequestClose}>Cancel</button>
                </div>
            </form>
        </Modal>
    );
};

export default JobPostingModal;
