import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import Modal from 'react-modal';
import '../styles/JobPostingModal.css';

Modal.setAppElement('#root');

const JobPostingModal = () => {
    const [jobTitle, setJobTitle] = useState('');
    const [jobDescription, setJobDescription] = useState('');
    const [requiredSkills, setRequiredSkills] = useState('');
    const [experience, setExperience] = useState('');
    const [salaryRange, setSalaryRange] = useState('');
    const [expiryDate, setExpiryDate] = useState('');
    const [jobType, setJobType] = useState('full-time');
    const [postedDate, setPostedDate] = useState(new Date().toISOString());

    const { id } = useParams(); // Lấy id từ URL để biết đây là chế độ sửa hay tạo mới
    const navigate = useNavigate();

    useEffect(() => {
        if (id) {
            // Nếu có id, fetch dữ liệu job để chỉnh sửa
            const fetchJobPosting = async () => {
                try {
                    const response = await fetch(`http://localhost:3001/job-postings/${id}`);
                    const data = await response.json();
                    setJobTitle(data.jobTitle);
                    setJobDescription(data.jobDescription);
                    setRequiredSkills(data.requiredSkills);
                    setExperience(data.experience);
                    setSalaryRange(data.salaryRange);
                    setExpiryDate(data.expiryDate);
                    setJobType(data.jobType);
                    setPostedDate(data.postedDate);
                } catch (error) {
                    console.error('Lỗi khi lấy job posting:', error);
                }
            };
            fetchJobPosting();
        }
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        const jobPosting = {
            jobTitle,
            jobDescription,
            requiredSkills,
            experience,
            salaryRange,
            expiryDate,
            jobType,
            postedDate
        };

        try {
            const method = id ? 'PUT' : 'POST';
            const url = id ? `http://localhost:3001/job-postings/${id}` : 'http://localhost:3001/job-postings';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(jobPosting)
            });

            if (!response.ok) {
                throw new Error('Lỗi khi lưu job posting');
            }

            navigate('/job-postings'); // Chuyển về trang danh sách job
        } catch (error) {
            console.error('Lỗi khi lưu job posting:', error);
        }
    };

    return (
        <Modal isOpen={isOpen} onRequestClose={onRequestClose} contentLabel="Create/Edit Job Posting">
            <h2>Create/Edit Job Posting</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="job-title">Job Title:</label>
                    <input
                        type="text"
                        id="job-title"
                        value={jobTitle}
                        onChange={(e) => setJobTitle(e.target.value)}
                        placeholder="Enter job title"
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="job-description">Job Description:</label>
                    <textarea
                        id="job-description"
                        value={jobDescription}
                        onChange={(e) => setJobDescription(e.target.value)}
                        placeholder="Enter job description"
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="required-skills">Required Skills:</label>
                    <input
                        type="text"
                        id="required-skills"
                        value={requiredSkills}
                        onChange={(e) => setRequiredSkills(e.target.value)}
                        placeholder="Enter required skills"
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="experience">Experience (Years):</label>
                    <input
                        type="text"
                        id="experience"
                        value={experience}
                        onChange={(e) => setExperience(e.target.value)}
                        placeholder="Enter required experience in years"
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="salary-range">Salary Range:</label>
                    <input
                        type="text"
                        id="salary-range"
                        value={salaryRange}
                        onChange={(e) => setSalaryRange(e.target.value)}
                        placeholder="Enter salary range"
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="expiry-date">Expiry Date:</label>
                    <input
                        type="date"
                        id="expiry-date"
                        value={expiryDate}
                        onChange={(e) => setExpiryDate(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="job-type">Job Type:</label>
                    <select
                        id="job-type"
                        value={jobType}
                        onChange={(e) => setJobType(e.target.value)}
                    >
                        <option value="full-time">Full-time</option>
                        <option value="part-time">Part-time</option>
                        <option value="internship">Internship</option>
                    </select>
                </div>

                <div className="button-container">
                    <button className="button" type="submit">Save</button>
                    <button className="button cancel" type="button" onClick={onRequestClose}>Cancel</button>
                </div>
            </form>
        </Modal>
    );
};

export default JobPostingModal;
