// src/components/ApplicantDetails.jsx
import React from 'react';
import { useLocation } from 'react-router-dom';
import '../styles/ApplicantDetails.css'; // Make sure to create this CSS file

const ApplicantDetails = () => {
    // Receive applicant data through state
    const location = useLocation();
    const applicant = location.state?.applicant || {
        name: 'John Doe',
        matchScore: '85%',
        cvSummary: 'This is the CV summary...',
    };

    return (
        <div className="container">
            <h2>Applicant Name: {applicant.name}</h2>
            <p><strong>Match Score:</strong> {applicant.matchScore}</p>

            <label htmlFor="cv-details">CV Details:</label>
            <textarea id="cv-details" readOnly value={applicant.cvSummary}></textarea>

            <div className="button-container">
                <button className="button" type="button">View Full CV</button>
                <button className="button shortlist" type="button">Shortlist</button>
                <button className="button reject" type="button">Reject</button>
            </div>
        </div>
    );
};

export default ApplicantDetails;
