// src/components/JobDetails.jsx
import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/JobDetails.css';

const JobDetails = () => {
    const location = useLocation();
    const navigate = useNavigate();

    // Kiểm tra dữ liệu từ location state
    const job = location.state?.job;

    // Log để kiểm tra nội dung của location.state
    console.log('Location state:', location.state);

    // Chức năng điều hướng trở lại
    const handleBack = () => {
        navigate(-1);
    };

    // Kiểm tra nếu không có dữ liệu công việc
    if (!job) {
        return <div>Job details not available.</div>;
    }

    return (
        <div className="container">
            <h2>Job Details</h2>
            <div className="job-info">
                <p><strong>Applicant Name:</strong> {job.applicantName}</p>
                <p><strong>Evaluation Score:</strong> {job.score}</p>
                <p><strong>Feedback:</strong> {job.feedback}</p>
            </div>

            <div className="buttons">
                <button onClick={handleBack} className="button back">Back</button>
            </div>
        </div>
    );
};

export default JobDetails;
