// src/components/JobSearch.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/JobSearch.css'; // Import CSS file for styling
import axios from 'axios'; // Import axios to make API calls

const JobSearch = () => {
    const navigate = useNavigate();
    const [jobs, setJobs] = useState([]);

    // Fetch job postings from API
    useEffect(() => {
        const fetchJobs = async () => {
            try {
                const response = await axios.get('http://localhost:3001/job-postings'); // Adjust the URL based on your setup
                setJobs(response.data);
            } catch (error) {
                console.error('Error fetching job postings:', error);
            }
        };

        fetchJobs();
    }, []);

    // Function to navigate to job details page
    const handleJobDetail = (job) => {
        navigate('/job-details', { state: { job } }); // Pass job data to job details page
    };

    return (
        <div className="container">
            <h2>Job Search</h2>

            <div className="search-bar">
                <input type="text" placeholder="Search by Keywords" />
            </div>
            <div className="filter-bar">
                <select>
                    <option value="">Job Type</option>
                    <option value="full-time">Full-time</option>
                    <option value="part-time">Part-time</option>
                </select>
                <select>
                    <option value="">Salary Range</option>
                    <option value="50k-60k">$50k - $60k</option>
                    <option value="60k-70k">$60k - $70k</option>
                </select>
            </div>

            <ul className="job-list">
                {/* Display only the first three job postings */}
                {jobs.slice(0, 3).map((job) => (
                    <li key={job.id} className="job-card"> {/* Ensure job.id is unique */}
                        <div>
                            <h4>{job.job_title}</h4> {/* Adjust based on your job data structure */}
                            <p>Company: {job.company} | Location: {job.location} | Salary: {job.salary_range}</p>
                        </div>
                        <button onClick={() => handleJobDetail(job)} className="apply-button">Apply</button>
                    </li>
                ))}
            </ul>

            {/* Add pagination or a link to see more jobs */}
            <div className="pagination">
                <button onClick={() => navigate('/job-search/page/1')}>1</button>
                <button onClick={() => navigate('/job-search/page/2')}>2</button>
                <button onClick={() => navigate('/job-search/page/3')}>3</button>
                {/* Add more buttons as needed */}
            </div>
        </div>
    );
};

export default JobSearch;
