// src/components/AiCvFiltering.jsx
import React, { useState, useEffect } from 'react';
import '../styles/AiCvFiltering.css';
import { useNavigate } from 'react-router-dom';

const AiCvFiltering = () => {
    const [applicants, setApplicants] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [jobTitleFilter, setJobTitleFilter] = useState('');
    const [experienceFilter, setExperienceFilter] = useState('');
    const [evaluationResult, setEvaluationResult] = useState(null); // State cho kết quả đánh giá

    const navigate = useNavigate();

    useEffect(() => {
        // Lấy danh sách ứng viên từ API
        const fetchApplicants = async () => {
            try {
                const response = await fetch('http://localhost:3001/api/applicants'); // Đảm bảo URL này khớp với route trong Express của bạn
                if (!response.ok) {
                    throw new Error('HTTP error! status: ' + response.status);
                }
                const data = await response.json();
                setApplicants(data); // Lưu danh sách ứng viên vào state
            } catch (error) {
                console.error('Error fetching applicants:', error);
            }
        };

        fetchApplicants();
    }, []);

    const filteredApplicants = applicants.filter(applicant => {
        return (
            (jobTitleFilter === '' || applicant.jobTitle === jobTitleFilter) &&
            (experienceFilter === '' || applicant.experience.startsWith(experienceFilter)) &&
            (searchTerm === '' || applicant.name.toLowerCase().includes(searchTerm.toLowerCase()))
        );
    });

    const handleShortlistClick = async (applicantId) => {
        try {
            const applicant = applicants.find(app => app.id === applicantId); // Lấy thông tin của ứng viên từ danh sách
            const cvData = {
                name: applicant.name,
                jobTitle: applicant.jobTitle,
                experience: applicant.experience,
                cvFile: applicant.cvFile // Giả sử bạn có trường cvFile trong đối tượng applicant
            };

            const response = await fetch('http://localhost:3001/api/evaluate-cv', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(cvData),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const evaluationResult = await response.json();
            console.log('CV Evaluation Result:', evaluationResult);

            // Gọi hàm để hiển thị kết quả đánh giá CV
            setEvaluationResult(evaluationResult);
        } catch (error) {
            console.error('Error during CV evaluation:', error);
        }
    };

    return (
        <div className="container">
            <h2>Recruiter Dashboard</h2>

            <div className="search-bar">
                <input
                    type="text"
                    placeholder="Search Applicants"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>
            <div className="filter-bar">
                <select value={jobTitleFilter} onChange={(e) => setJobTitleFilter(e.target.value)}>
                    <option value="">Filter by Job Title</option>
                    <option value="Software Engineer">Software Engineer</option>
                    <option value="Data Analyst">Data Analyst</option>
                </select>
                <select value={experienceFilter} onChange={(e) => setExperienceFilter(e.target.value)}>
                    <option value="">Filter by Experience</option>
                    <option value="0-2">0-2 Years</option>
                    <option value="3-5">3-5 Years</option>
                    <option value="5+">5+ Years</option>
                </select>
            </div>

            <ul className="applicant-list">
                {filteredApplicants.map(applicant => (
                    <li key={applicant.id} className="applicant-card">
                        <div>
                            <h4>{applicant.name}</h4>
                            <p>Job Title: {applicant.jobTitle} | Experience: {applicant.experience}</p>
                        </div>
                        <div className="action-buttons">
                            <button onClick={() => handleShortlistClick(applicant.id)}>Shortlist</button>
                            <button>Reject</button>
                        </div>
                    </li>
                ))}
            </ul>

            {evaluationResult && (
                <div className="evaluation-result">
                    <h3>Evaluation Result for {evaluationResult.applicantName}</h3>
                    <p>Score: {evaluationResult.score}</p>
                    <p>Feedback: {evaluationResult.feedback}</p>
                </div>
            )}
        </div>
    );
};

export default AiCvFiltering;
