// src/components/CandidateProfile.jsx
import React from 'react';
import '../styles/CandidateProfile.css'; // Tạo file CSS riêng nếu cần

const CandidateProfile = () => {
    const handleSaveChanges = (event) => {
        event.preventDefault(); // Ngăn chặn việc reload trang

        const fullname = document.getElementById('full-name').value;
        const email = document.getElementById('email').value;
        const picture = document.getElementById('profile-picture').value;
        const experience = document.getElementById('work-experience').value;
        const education = document.getElementById('education').value;

        if (fullname === "" || picture === "" || email === "" || experience === "" || education === "") {
            alert('Please fill all the boxes');
        } else {
            alert('Save Successful');
            window.location.href = '/job-posting-list'; // Đảm bảo đường dẫn đúng với router của bạn
        }
    };

    return (
        <div className="container">
            <h2>Candidate Profile</h2>
            <form onSubmit={handleSaveChanges}>
                <div className="form-group">
                    <label htmlFor="full-name">Full Name:</label>
                    <input type="text" id="full-name" placeholder="Enter your full name" />
                </div>

                <div className="form-group">
                    <label htmlFor="email">Email Address:</label>
                    <input type="email" id="email" placeholder="Enter your email address" />
                </div>

                <div className="form-group">
                    <label htmlFor="profile-picture">Upload Profile Picture:</label>
                    <input type="file" id="profile-picture" />
                </div>

                <div className="form-group">
                    <label htmlFor="work-experience">Work Experience:</label>
                    <textarea id="work-experience" placeholder="Detail your work experience"></textarea>
                </div>

                <div className="form-group">
                    <label htmlFor="education">Education:</label>
                    <textarea id="education" placeholder="Detail your education background"></textarea>
                </div>

                <div className="button-container">
                    <button className="button" type="submit">Save Changes</button>
                    <button className="button cancel" type="button">Cancel</button>
                </div>
            </form>
        </div>
    );
};

export default CandidateProfile;
