// src/components/JobSearch.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/JobSearch.css'; // Import CSS file for styling

const JobSearch = () => {
    const navigate = useNavigate();
    const [jobPostings, setJobPostings] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchJobPostings = async () => {
            try {
                const response = await axios.get('http://localhost:3001/job-postings');
                setJobPostings(response.data);
            } catch (err) {
                console.error('Lỗi khi lấy danh sách công việc:', err);
                setError('Không thể lấy danh sách công việc');
            } finally {
                setLoading(false);
            }
        };

        fetchJobPostings();
    }, []);

    const handleJobDetail = (jobId) => {
        navigate(`/job-details/${jobId}`); // Điều hướng đến trang chi tiết công việc
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="container">
            <h2>Tìm Kiếm Việc Làm</h2>

            <div className="search-bar">
                <input type="text" placeholder="Tìm kiếm theo từ khóa" />
            </div>
            <div className="filter-bar">
                <select>
                    <option value="">Loại công việc</option>
                    <option value="full-time">Toàn thời gian</option>
                    <option value="part-time">Bán thời gian</option>
                </select>
                <select>
                    <option value="">Mức lương</option>
                    <option value="50k-60k">$50k - $60k</option>
                    <option value="60k-70k">$60k - $70k</option>
                </select>
            </div>

            <ul className="job-list">
                {jobPostings.map(job => (
                    <li key={job.id} className="job-card">
                        <div>
                            <h4>{job.job_title}</h4>
                            <p>Công ty: {job.company_name} | Địa điểm: {job.location} | Lương: {job.salary_range}</p>
                        </div>
                        <button onClick={() => handleJobDetail(job.id)} className="apply-button">Nộp đơn</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default JobSearch;
