// src/components/AiCvFiltering.jsx
import React, { useState } from 'react';
import '../styles/AiCvFiltering.css'; // Import the CSS file

const AiCvFiltering = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [jobTitleFilter, setJobTitleFilter] = useState('');
    const [experienceFilter, setExperienceFilter] = useState('');

    const applicants = [
        { name: 'John Doe', jobTitle: 'Software Engineer', experience: '3 Years' },
        { name: 'Jane Smith', jobTitle: 'Data Analyst', experience: '5 Years' },
        // Add more applicants as needed
    ];

    const filteredApplicants = applicants.filter(applicant => {
        const matchesSearchTerm = applicant.name.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesJobTitle = jobTitleFilter ? applicant.jobTitle === jobTitleFilter : true;
        const matchesExperience = experienceFilter ? applicant.experience.startsWith(experienceFilter) : true;
        return matchesSearchTerm && matchesJobTitle && matchesExperience;
    });

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    const handleJobTitleChange = (e) => {
        setJobTitleFilter(e.target.value);
    };

    const handleExperienceChange = (e) => {
        setExperienceFilter(e.target.value);
    };

    const handleShortlist = (applicantName) => {
        alert(`Shortlisted ${applicantName}`);
    };

    const handleReject = (applicantName) => {
        alert(`Rejected ${applicantName}`);
    };

    return (
        <div className="container">
            <h2>Recruiter Dashboard</h2>

            <div className="search-bar">
                <input
                    type="text"
                    placeholder="Search Applicants"
                    value={searchTerm}
                    onChange={handleSearchChange}
                />
            </div>
            <div className="filter-bar">
                <select onChange={handleJobTitleChange}>
                    <option value="">Filter by Job Title</option>
                    <option value="Software Engineer">Software Engineer</option>
                    <option value="Data Analyst">Data Analyst</option>
                </select>
                <select onChange={handleExperienceChange}>
                    <option value="">Filter by Experience</option>
                    <option value="0-2">0-2 Years</option>
                    <option value="3-5">3-5 Years</option>
                    <option value="5+">5+ Years</option>
                </select>
            </div>

            <ul className="applicant-list">
                {filteredApplicants.map((applicant, index) => (
                    <li key={index} className="applicant-card">
                        <div>
                            <h4>{applicant.name}</h4>
                            <p>Job Title: {applicant.jobTitle} | Experience: {applicant.experience}</p>
                        </div>
                        <div className="action-buttons">
                            <button onClick={() => handleShortlist(applicant.name)}>Shortlist</button>
                            <button onClick={() => handleReject(applicant.name)}>Reject</button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AiCvFiltering;
