import React, { useState } from 'react';
import '../styles/JobPostingList.css'; // Đảm bảo tạo file CSS nếu cần
import JobPostingModal from './JobPostingModal';

const JobPostingList = () => {
    const [modalIsOpen, setModalIsOpen] = useState(false);
    const [jobPostings, setJobPostings] = useState([]); // State để lưu danh sách job postings

    const openModal = () => {
        setModalIsOpen(true);
    };

    const closeModal = () => {
        setModalIsOpen(false);
    };

    const handleAddJobPosting = (newJobPosting) => {
        setJobPostings([...jobPostings, newJobPosting]); // Thêm job posting mới vào danh sách
    };

    return (
        <div className="container">
            <h2>Job Posting List</h2>
            <button onClick={openModal} className="button">Create New Job Posting</button>

            <div className="search-bar">
                <input type="text" placeholder="Search by Job Title" />
            </div>
            <div className="filter-bar">
                <select>
                    <option value="">Filter by Status</option>
                    <option value="active">Active</option>
                    <option value="expired">Expired</option>
                </select>
            </div>

            <table>
                <thead>
                <tr>
                    <th>Job Title</th>
                    <th>Posted Date</th>
                    <th>Status</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {jobPostings.map((job, index) => (
                    <tr key={index}>
                        <td>{job.jobTitle}</td>
                        <td>{new Date().toISOString().split('T')[0]}</td> {/* Ngày hiện tại */}
                        <td>Active</td>
                        <td className="actions">
                            <a href="#" onClick={() => openModal()}>Edit</a>
                            <a href="#">Delete</a>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
            <JobPostingModal
                isOpen={modalIsOpen}
                onRequestClose={closeModal}
                onAddJobPosting={handleAddJobPosting} // Truyền hàm thêm job posting vào modal
            />
        </div>
    );
};

export default JobPostingList;
