import React, { useState, useEffect } from 'react';
import '../styles/AiCvFiltering.css';
import { useNavigate } from 'react-router-dom';

const AiCvFiltering = () => {
    const [applicants, setApplicants] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [jobTitleFilter, setJobTitleFilter] = useState('');
    const [experienceFilter, setExperienceFilter] = useState('');

    const navigate = useNavigate();

    useEffect(() => {
        // Lấy danh sách ứng viên từ API
        const fetchApplicants = async () => {
            try {
                const response = await fetch('http://localhost:3001/api/applicants'); // Cập nhật URL
                if (!response.ok) {
                    throw new Error('HTTP error! status: ' + response.status);
                }
                const data = await response.json();
                setApplicants(data); // Lưu danh sách ứng viên vào state
            } catch (error) {
                console.error('Lỗi khi lấy danh sách ứng viên:', error);
            }
        };

        fetchApplicants();
    }, []);

    const filteredApplicants = applicants.filter(applicant => {
        return (
            (jobTitleFilter === '' || applicant.jobTitle === jobTitleFilter) &&
            (experienceFilter === '' || applicant.experience.startsWith(experienceFilter)) &&
            (searchTerm === '' || applicant.name.toLowerCase().includes(searchTerm.toLowerCase()))
        );
    });

    const handleShortlistClick = (id) => {
        navigate(`/applicant-details/}`);
    };

    return (
        <div className="container">
            <h2>Bảng điều khiển nhà tuyển dụng</h2>

            <div className="search-bar">
                <input
                    type="text"
                    placeholder="Tìm kiếm ứng viên"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>
            <div className="filter-bar">
                <select value={jobTitleFilter} onChange={(e) => setJobTitleFilter(e.target.value)}>
                    <option value="">Lọc theo chức danh công việc</option>
                    <option value="Software Engineer">Kỹ sư phần mềm</option>
                    <option value="Data Analyst">Nhà phân tích dữ liệu</option>
                    {/* Các tùy chọn công việc khác nếu cần */}
                </select>
                <select value={experienceFilter} onChange={(e) => setExperienceFilter(e.target.value)}>
                    <option value="">Lọc theo kinh nghiệm</option>
                    <option value="0-2">0-2 năm</option>
                    <option value="3-5">3-5 năm</option>
                    <option value="5+">5+ năm</option>
                </select>
            </div>

            <ul className="applicant-list">
                {filteredApplicants.map(applicant => (
                    <li key={applicant.id} className="applicant-card">
                        <div>
                            <h4>{applicant.name}</h4>
                            <p>Chức danh: {applicant.jobTitle} | Kinh nghiệm: {applicant.experience}</p>
                        </div>
                        <div className="action-buttons">
                            <button onClick={() => handleShortlistClick(applicant.id)}>Rút gọn</button>
                            <button>Từ chối</button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AiCvFiltering;
