    import React from 'react';
    import { useNavigate } from 'react-router-dom';

    const Login = ({ toggleForm }) => {
        const navigate = useNavigate();

        const handleSubmit = async (e) => {
            e.preventDefault();
            const email = e.target.email.value;
            const password = e.target.password.value;

            try {
                const res = await fetch('http://localhost:3000/login', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ email, password })
                });

                if (res.ok) {
                    const data = await res.json();  // Đảm bảo chuyển đổi sang JSON
                    alert("Đăng nhập thành công");
                    if (data.user_type === 'applicant') {
                        window.location.href = '/ApplicantHome';
                    } else if (data.user_type === 'recruiter') {
                        window.location.href = '/RecruiterHome';
                    }
                } else {
                    alert("Sai email hoặc mật khẩu.");
                }
            } catch (error) {
                console.error("Có lỗi xảy ra:", error);
                alert("Có lỗi xảy ra khi đăng nhập.");
            }
        };

        const handleForgotPassword = () => {
            navigate('/forgot-password'); // Điều hướng đến trang ForgotPassword
        };

        return (
            <div className="container">
                <h2>Login</h2>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="email">Email Address</label>
                    <input type="email" id="email" name="email" required />

                    <label htmlFor="password">Password</label>
                    <input type="password" id="password" name="password" required />

                    <div className="checkbox">
                        <label>
                            <input type="checkbox"/> Remember Me
                        </label>
                        <a href="" onClick={handleForgotPassword}>Forgot Password?</a>
                    </div>

                    <button type="submit" className="button">Login</button>

                    <div className="links">
                        <span>Don't have an account?</span>
                        <a href="#" onClick={toggleForm}>Register Now</a>
                    </div>
                </form>
            </div>
        );
    };

    export default Login;
