// src/components/JobDetails.jsx
import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/JobDetails.css'; // Import the CSS file for styling
const job = location.state?.job; // Kiểm tra nếu state có job
console.log('Location state:', location.state); // Thêm log để kiểm tra

const JobDetails = () => {
    const location = useLocation();
    const navigate = useNavigate();

    // Get job data from location state
    const job = location.state?.job; // Optional chaining to avoid errors if state is undefined

    // Function to navigate to application form page
    const handleApplicationForm = () => {
        navigate('/application-form'); // Adjust the route as needed for your application form page
    };

    // Check if job data is available
    if (!job) {
        return <div>Job details not available.</div>; // Handle case when job is not found
    }

    return (
        <div className="container">
            <h2>Job Details</h2>
            <div className="job-info">
                <p><strong>Job Title:</strong> {job.job_title}</p>
                <p><strong>Company Name:</strong> {job.company}</p>
                <p><strong>Job Description:</strong></p>
                <p>{job.job_description}</p>
                <p><strong>Required Skills:</strong>  {job.required_skills}</p>
                <p><strong>Experience Required:</strong> {job.experience} years</p>
                <p><strong>Salary Range:</strong> {job.salary_range}</p>
                <p><strong>Expiry Date:</strong> {job.expiry_date}</p>
                <p><strong>Job Type:</strong> {job.job_type}</p>
                <p><strong>Posted Date:</strong> {job.posted_date}</p>
                <p><strong>Recruiter ID:</strong> {job.recruiter_id}</p>
            </div>

            <div className="buttons">
                <button onClick={handleApplicationForm} className="button apply-now">Apply Now</button>
                <button className="button save">Save Job</button>
            </div>
        </div>
    );
};

export default JobDetails;
