import React, { useState } from 'react';
import '../styles/CandidateProfile.css'; // Tạo file CSS riêng nếu cần

const CandidateProfile = () => {
    const [fullname, setFullname] = useState('');
    const [email, setEmail] = useState('');
    const [picture, setPicture] = useState(null);
    const [experience, setExperience] = useState('');
    const [education, setEducation] = useState('');

    const handleSaveChanges = async (event) => {
        event.preventDefault(); // Ngăn chặn việc reload trang

        if (!fullname || !picture || !email || !experience || !education) {
            alert('Please fill all the boxes');
            return;
        }

        // Tạo đối tượng FormData để gửi tệp hình ảnh và thông tin
        const formData = new FormData();
        formData.append('fullname', fullname);
        formData.append('email', email);
        formData.append('profilePicture', picture);
        formData.append('workExperience', experience);
        formData.append('education', education);

        try {
            const response = await fetch('http://localhost:3001/api/candidate-profile', {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                alert('Save Successful');
                window.location.href = '/job-posting-list'; // Đảm bảo đường dẫn đúng với router của bạn
            } else {
                alert('Save Failed: ' + (await response.json()).message);
            }
        } catch (error) {
            console.error('Error:', error);
            alert('An error occurred while saving the profile.');
        }
    };

    return (
        <div className="container">
            <h2>Candidate Profile</h2>
            <form onSubmit={handleSaveChanges}>
                <div className="form-group">
                    <label htmlFor="full-name">Full Name:</label>
                    <input
                        type="text"
                        id="full-name"
                        placeholder="Enter your full name"
                        value={fullname}
                        onChange={(e) => setFullname(e.target.value)}
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="email">Email Address:</label>
                    <input
                        type="email"
                        id="email"
                        placeholder="Enter your email address"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="profile-picture">Upload Profile Picture:</label>
                    <input
                        type="file"
                        id="profile-picture"
                        onChange={(e) => setPicture(e.target.files[0])}
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="work-experience">Work Experience:</label>
                    <textarea
                        id="work-experience"
                        placeholder="Detail your work experience"
                        value={experience}
                        onChange={(e) => setExperience(e.target.value)}
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="education">Education:</label>
                    <textarea
                        id="education"
                        placeholder="Detail your education background"
                        value={education}
                        onChange={(e) => setEducation(e.target.value)}
                    />
                </div>

                <div className="button-container">
                    <button className="button" type="submit">Save Changes</button>
                    <button className="button cancel" type="button">Cancel</button>
                </div>
            </form>
        </div>
    );
};

export default CandidateProfile;
