// src/components/JobDetails.jsx
import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/JobDetails.css'; // Import the CSS file for styling

const JobDetails = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const { job } = location.state; // Nhận thông tin công việc từ state

    // Function to navigate to application form page
    const handleApplicationForm = () => {
        navigate('/application-form'); // Adjust the route as needed for your application form page
    };

    return (
        <div className="container">
            <h2>Job Details</h2>
            <div className="job-info">
                <p><strong>Job Title:</strong> {job.job_title}</p> {/* Hiển thị tiêu đề công việc */}
                <p><strong>Company Name:</strong> {job.company}</p> {/* Hiển thị tên công ty */}
                <p><strong>Job Description:</strong></p>
                <textarea
                    readOnly
                    value={job.description} // Hiển thị mô tả công việc từ DB
                />
                <p><strong>Required Skills:</strong> {job.skills.join(', ')}</p> {/* Hiển thị kỹ năng yêu cầu */}
                <p><strong>Salary Range:</strong> {job.salary_range}</p> {/* Hiển thị khoảng lương */}
            </div>

            <div className="buttons">
                <button onClick={handleApplicationForm} className="button apply-now">Apply Now</button>
                <button className="button save">Save Job</button>
            </div>
        </div>
    );
};

export default JobDetails;
