import React, { useState } from 'react';
import axios from 'axios';

const Register = ({ toggleForm }) => {
    const [formData, setFormData] = useState({
        fullname: '',
        email: '',
        password: '',
        userType: 'applicant',
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Gửi yêu cầu đăng ký tới backend
            const response = await axios.post('http://localhost:8000/api/register/', formData);
            if (response.data.success) {
                toggleForm();  // Chuyển sang form đăng nhập nếu đăng ký thành công
            }
        } catch (error) {
            console.error("Registration Error:", error);
        }
    };

    return (
        <div className="container">
            <h2>Register</h2>
            <form onSubmit={handleSubmit}>
                <label htmlFor="fullname">Full Name</label>
                <input type="text" id="fullname" name="fullname" onChange={handleChange} required />

                <label htmlFor="email">Email Address</label>
                <input type="email" id="email" name="email" onChange={handleChange} required />

                <label htmlFor="password">Password</label>
                <input type="password" id="password" name="password" onChange={handleChange} required />

                <label htmlFor="userType">User Type</label>
                <select id="userType" name="userType" onChange={handleChange} required>
                    <option value="applicant">Applicant</option>
                    <option value="recruiter">Recruiter</option>
                </select>

                <button type="submit" className="button">Register</button>

                <div className="links">
                    <span>Already have an account?</span>
                    <a href="#" onClick={toggleForm}>Login</a>
                </div>
            </form>
        </div>
    );
};

export default Register;
