// src/components/JobSearch.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/JobSearch.css'; // Import CSS file for styling

const JobSearch = () => {
    const navigate = useNavigate();

    // Function to navigate to job details page
    const handleJobDetail = () => {
        navigate('/job-details'); // Replace with your job details route
    };

    return (
        <div className="container">
            <h2>Job Search</h2>

            <div className="search-bar">
                <input type="text" placeholder="Search by Keywords" />
            </div>
            <div className="filter-bar">

                <select>
                    <option value="">Job Type</option>
                    <option value="full-time">Full-time</option>
                    <option value="part-time">Part-time</option>
                </select>
                <select>
                    <option value="">Salary Range</option>
                    <option value="50k-60k">$50k - $60k</option>
                    <option value="60k-70k">$60k - $70k</option>
                </select>
            </div>

            <ul className="job-list">
                <li className="job-card">
                    <div>
                        <h4>Software Engineer</h4>
                        <p>Company: ABC Corp | Location: New York | Salary: $60k</p>
                    </div>
                    <button onClick={handleJobDetail} className="apply-button">Apply</button>
                </li>
                <li className="job-card">
                    <div>
                        <h4>Data Analyst</h4>
                        <p>Company: XYZ Ltd | Location: London | Salary: $55k</p>
                    </div>
                    <button onClick={handleJobDetail} className="apply-button">Apply</button>
                </li>
            </ul>
        </div>
    );
};

export default JobSearch;
