// JobSearch.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const JobSearch = () => {
    const [jobPostings, setJobPostings] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchJobPostings = async () => {
            try {
                const response = await axios.get('http://localhost:3001/job-postings');
                setJobPostings(response.data);
            } catch (err) {
                console.error('Lỗi khi lấy danh sách công việc:', err);
                setError('Không thể lấy danh sách công việc');
            } finally {
                setLoading(false);
            }
        };

        fetchJobPostings();
    }, []);

    if (loading) {
        return <p>Đang tải...</p>;
    }

    if (error) {
        return <p>{error}</p>;
    }

    return (
        <div>
            <h2>Danh Sách Công Việc</h2>
            <ul>
                {jobPostings.map((job) => (
                    <li key={job.id}>
                        <h3>{job.job_title}</h3>
                        <p>{job.job_description}</p>
                        <p>Yêu cầu: {job.required_skills}</p>
                        <p>Kinh nghiệm: {job.experience}</p>
                        <p>Mức lương: {job.salary_range}</p>
                        <p>Ngày hết hạn: {job.expiry_date}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default JobSearch;
