import React from 'react';

const Login = ({ toggleForm }) => {

    const handleSubmit = async (e) => {
        e.preventDefault();
        const email = e.target.email.value;
        const password = e.target.password.value;

        try {
            const res = await fetch('http://localhost:3001/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email, password })
            });

            const data = await res.json();
            if (data.token) {
                localStorage.setItem('token', data.token);

                // Điều hướng tới trang theo quyền
                if (data.user_type === 'applicant') {
                    window.location.href = '/applicant-home';
                } else {
                    window.location.href = '/recruiter-home';
                }
            } else {
                alert("Đăng nhập thất bại. Vui lòng kiểm tra email hoặc mật khẩu.");
            }
        } catch (error) {
            console.error("Có lỗi xảy ra:", error);
            alert("Có lỗi xảy ra khi đăng nhập.");
        }
    };

    return (
        <div className="container">
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
                <label htmlFor="email">Email Address</label>
                <input type="email" id="email" name="email" required />

                <label htmlFor="password">Password</label>
                <input type="password" id="password" name="password" required />

                <div className="checkbox">
                    <label>
                        <input type="checkbox" /> Remember Me
                    </label>
                    <a href="#">Forgot Password?</a>
                </div>

                <button type="submit" className="button">Login</button>

                <div className="links">
                    <span>Don't have an account?</span>
                    <a href="#" onClick={toggleForm}>Register Now</a>
                </div>
            </form>
        </div>
    );
};

export default Login;
