import React, { useState } from 'react';

const Register = ({ toggleForm }) => {
    const [isLoading, setIsLoading] = useState(false);
    const [emailError, setEmailError] = useState('');
    const [emailValid, setEmailValid] = useState(true);
    const [email, setEmail] = useState(''); // Add state for email input

    // Function to check if the email already exists
    const checkEmail = async (email) => {
        try {
            const response = await fetch(`http://localhost:3001/check-email?email=${email}`);
            const data = await response.json();

            if (response.status === 400) {
                setEmailError(data.message); // Show the error message if email exists
                setEmailValid(false);
            } else {
                setEmailError(''); // Clear the error if email is valid
                setEmailValid(true);
            }
        } catch (error) {
            console.error('Lỗi kiểm tra email:', error);
            setEmailError('Có lỗi xảy ra khi kiểm tra email.');
            setEmailValid(false);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const fullname = e.target.fullname.value;
        const email = e.target.email.value;
        const password = e.target.password.value;
        const confirmPassword = e.target['confirm-password'].value;
        const user_type = e.target['user-type'].value;

        // Password validation
        if (password !== confirmPassword) {
            alert("Mật khẩu và xác nhận mật khẩu không khớp.");
            return;
        }

        if (password.length < 8) {
            alert("Mật khẩu phải có ít nhất 8 ký tự.");
            return;
        }

        if (!emailValid) {
            alert("Email không hợp lệ. Vui lòng nhập email khác.");
            return;
        }

        try {
            setIsLoading(true);
            const res = await fetch('http://localhost:3001/register', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ fullname, email, password, user_type })
            });

            const data = await res.json();

            if (res.ok) {
                alert("Đăng ký thành công");
                window.location.href = '/'; // Redirect after success
            } else {
                alert(`Đăng ký thất bại: ${data.message || 'Vui lòng kiểm tra thông tin.'}`);
            }
        } catch (error) {
            console.error("Có lỗi xảy ra:", error);
            alert("Có lỗi xảy ra khi đăng ký.");
        } finally {
            setIsLoading(false);
        }
    };

    // Handle email input change and reset error state
    const handleEmailChange = (e) => {
        setEmail(e.target.value);
        setEmailError(''); // Clear the error when the user starts typing a new email
        setEmailValid(true); // Reset email validity
    };

    return (
        <div className="container">
            <h2>Register</h2>
            <form onSubmit={handleSubmit}>
                <label htmlFor="fullname">Full Name</label>
                <input type="text" id="fullname" name="fullname" required />

                <label htmlFor="email">Email Address</label>
                <input
                    type="email"
                    id="email"
                    name="email"
                    required
                    value={email} // Controlled input value
                    onChange={handleEmailChange} // Reset error when email changes
                    onBlur={() => checkEmail(email)} // Check email on blur
                />
                {emailError && <p style={{ color: 'red' }}>{emailError}</p>}

                <label htmlFor="password">Password</label>
                <input type="password" id="password" name="password" required />

                <label htmlFor="confirm-password">Confirm Password</label>
                <input type="password" id="confirm-password" name="confirm-password" required />

                <label htmlFor="user-type">User Type</label>
                <select id="user-type" name="user-type" required>
                    <option value="applicant">Applicant</option>
                    <option value="recruiter">Recruiter</option>
                </select>

                <button type="submit" className="button" disabled={isLoading || !emailValid}>
                    {isLoading ? 'Registering...' : 'Register'}
                </button>

                <div className="links">
                    <span>Already have an account?</span>
                    <a href="#" onClick={toggleForm}>Login</a>
                </div>
            </form>
        </div>
    );
};

export default Register;
