import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/JobPostingList.css'; // Đảm bảo tạo file CSS nếu cần

const JobPostingList = () => {
    
    const [jobPostings, setJobPostings] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [filterStatus, setFilterStatus] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchJobPostings = async () => {
            try {
                const response = await fetch('http://localhost:3001/job-postings');
                const data = await response.json();
                setJobPostings(data);
            } catch (error) {
                console.error('Lỗi khi lấy job postings:', error);
            }
        };
        fetchJobPostings();
    }, []);

    const handleEditJobPosting = (id) => {
        navigate(`/job-postings/edit/${id}`); // Điều hướng đến trang sửa job với id
    };

    const handleCreateJobPosting = () => {
        navigate('/job-postings/new'); // Điều hướng đến trang tạo job mới
    };

    const handleDeleteJobPosting = (id) => {
        const updatedPostings = jobPostings.filter((job) => job.id !== id);
        setJobPostings(updatedPostings);
    };

    const filteredJobPostings = jobPostings.filter(job => {
        const isTitleMatch = job.job_title.toLowerCase().includes(searchTerm.toLowerCase());
        const isStatusMatch = filterStatus === '' ||
            (filterStatus === 'active' && new Date(job.expiryDate) > new Date()) ||
            (filterStatus === 'expired' && new Date(job.expiryDate) <= new Date());
        return isTitleMatch && isStatusMatch;
    });

    return (
        <div className="container">
            <h2>Job Posting List</h2>
            <button onClick={handleCreateJobPosting} className="button">Create New Job Posting</button>

            <div className="search-bar">
                <input
                    type="text"
                    placeholder="Search by Job Title"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>
            <div className="filter-bar">
                <select value={filterStatus} onChange={(e) => setFilterStatus(e.target.value)}>
                    <option value="">Filter by Status</option>
                    <option value="active">Active</option>
                    <option value="expired">Expired</option>
                </select>
            </div>

            <table>
                <thead>
                <tr>
                    <th>Job Title</th>
                    <th>Posted Date</th>
                    <th>Status</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {filteredJobPostings.map((job) => (
                    <tr key={job.id}>
                        <td>{job.job_title}</td>
                        <td>{new Date(job.posted_date).toLocaleDateString()}</td>
                        <td>{new Date(job.expiryDate) > new Date() ? 'Active' : 'Expired'}</td>
                        <td className="actions">
                            <button onClick={() => handleEditJobPosting(job.id)}>Edit</button>
                            <button onClick={() => handleDeleteJobPosting(job.id)}>Delete</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
}

export default JobPostingList;
