import React, { useState } from 'react';

const Register = ({ toggleForm }) => {
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const fullname = e.target.fullname.value;
        const email = e.target.email.value;
        const password = e.target.password.value;
        const confirmPassword = e.target['confirm-password'].value;
        const user_type = e.target['user-type'].value;

        // Basic password validation
        if (password !== confirmPassword) {
            setError("Mật khẩu và xác nhận mật khẩu không khớp.");
            return;
        }

        if (password.length < 8) {
            setError("Mật khẩu phải có ít nhất 8 ký tự.");
            return;
        }

        // Email validation regex
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(email)) {
            setError("Vui lòng nhập địa chỉ email hợp lệ.");
            return;
        }

        try {
            setIsLoading(true);
            const res = await fetch('http://localhost:3001/register', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ fullname, email, password, user_type })
            });

            const data = await res.json();

            if (res.ok) {
                alert("Đăng ký thành công");
                window.location.href = '/'; // Redirect after success
            } else {
                // Show the error message received from the backend
                setError(data.message || 'Vui lòng kiểm tra thông tin.');
            }
        } catch (error) {
            console.error("Có lỗi xảy ra:", error);
            setError("Có lỗi xảy ra khi đăng ký.");
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="container">
            <h2>Register</h2>
            <form onSubmit={handleSubmit}>
                <label htmlFor="fullname">Full Name</label>
                <input type="text" id="fullname" name="fullname" required />

                <label htmlFor="email">Email Address</label>
                <input type="email" id="email" name="email" required />

                <label htmlFor="password">Password</label>
                <input type="password" id="password" name="password" required />

                <label htmlFor="confirm-password">Confirm Password</label>
                <input type="password" id="confirm-password" name="confirm-password" required />

                <label htmlFor="user-type">User Type</label>
                <select id="user-type" name="user-type" required>
                    <option value="applicant">Applicant</option>
                    <option value="recruiter">Recruiter</option>
                </select>

                <button type="submit" className="button" disabled={isLoading}>
                    {isLoading ? 'Đang Đăng Ký...' : 'Đăng Ký'}
                </button>

                {error && <div className="error-message">{error}</div>} {/* Hiển thị thông báo lỗi */}

                <div className="links">
                    <span>Đã có tài khoản?</span>
                    <a href="#" onClick={toggleForm}>Đăng Nhập</a>
                </div>
            </form>
        </div>
    );
};

export default Register;
